function f24(elemento)
{
	if(document.getElementById(elemento).style.visibility=="hidden"){
	document.getElementById(elemento).style.visibility="visible";
	}
	else if(document.getElementById(elemento).style.visibility=="visible")
	document.getElementById(elemento).style.visibility="hidden";
}
function f45(elemento)
{
	if(document.getElementById(elemento).style.visibility=="hidden"){
	document.getElementById(elemento).style.visibility="visible";
	}
}
function f45n(elemento)
{
	if(document.getElementById(elemento).style.visibility=="visible"){
	document.getElementById(elemento).style.visibility="hidden";
	}
}



